const allItems = [];
const seen = new Set();

for (const row of $input.all()) {
  try {
    const data = JSON.parse(row.json.content);
    if (data.items) {
      for (const item of data.items) {
        const key = item.title.toLowerCase().substring(0, 50);
        if (!seen.has(key)) {
          seen.add(key);
          allItems.push(item);
        }
      }
    }
  } catch(e) {}
}

// Sort by weighted score (recency + engagement)
allItems.sort((a, b) => {
  const scoreA = (a.score || 0) * (1 / Math.max(1, (a.age_hours || 1) / 12));
  const scoreB = (b.score || 0) * (1 / Math.max(1, (b.age_hours || 1) / 12));
  return scoreB - scoreA;
});

return [{ json: { items: allItems.slice(0, 60) } }];

const response = $input.first().json;
const text = response.response?.text || response.text || JSON.stringify(response);

let items = [];
try {
  const match = text.match(/\[.*\]/s);
  if (match) items = JSON.parse(match[0]);
} catch(e) {
  return [{ json: { error: 'Failed to parse AI response', raw: text } }];
}

// Organize by section
const sections = {
  BREAKING: [],
  TOP_STORIES: [],
  CREATIVE_TOOLS: [],
  CODING_TOOLS: [],
  WRITING_TOOLS: [],
  LIFE_PRODUCTIVITY: [],
  RESEARCH_FRONTIER: [],
  COMMUNITY_PULSE: []
};

for (const item of items) {
  const section = item.section?.toUpperCase().replace(/ /g, '_');
  if (sections[section]) {
    sections[section].push(item);
  }
}

// Sort each section by priority
for (const key of Object.keys(sections)) {
  sections[key].sort((a, b) => (b.priority || 5) - (a.priority || 5));
}

const date = new Date().toLocaleDateString('en-US', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' });

return [{
  json: {
    date,
    generated_at: new Date().toISOString(),
    sections,
    total_items: items.length
  }
}];

const d = $input.first().json;
const s = d.sections;

const formatScore = (score) => score >= 1000 ? (score/1000).toFixed(1) + 'k' : String(score || 0);

const sourceBadge = (source) => {
  const colors = { reddit: '#FF4500', hackernews: '#FF6600', github: '#333', devto: '#0A0A0A', news: '#2563eb', arxiv: '#b31b1b', youtube: '#FF0000' };
  const names = { reddit: 'Reddit', hackernews: 'HN', github: 'GitHub', devto: 'Dev.to', news: 'News', arxiv: 'ArXiv', youtube: 'YouTube' };
  return `<span style="background:${colors[source] || '#666'};color:white;padding:2px 8px;border-radius:4px;font-size:11px;font-weight:600;">${names[source] || source}</span>`;
};

const renderTopStories = (items) => {
  if (!items?.length) return '';
  return `<div style="margin-bottom:40px;"><div style="display:flex;align-items:center;margin-bottom:20px;"><span style="font-size:24px;margin-right:10px;">ðŸ”¥</span><h2 style="margin:0;font-size:20px;font-weight:700;color:#111;">Top Stories</h2></div>${items.map(i => `<div style="margin-bottom:20px;padding:20px;background:#fafafa;border-radius:12px;border:1px solid #e5e7eb;"><div style="margin-bottom:10px;">${sourceBadge(i.source)}</div><a href="${i.url}" style="text-decoration:none;"><h3 style="margin:0 0 8px;font-size:17px;font-weight:600;color:#111;line-height:1.4;">${(i.title || '').substring(0,100)}</h3></a><p style="margin:0 0 10px;font-size:14px;color:#4b5563;line-height:1.5;">${(i.summary || '').substring(0,150)}</p><div style="font-size:13px;color:#6b7280;">â–² ${formatScore(i.priority * 50)} points</div></div>`).join('')}</div>`;
};

const renderSection = (emoji, title, items, style = 'list') => {
  if (!items?.length) return '';
  const header = `<div style="display:flex;align-items:center;margin-bottom:20px;"><span style="font-size:24px;margin-right:10px;">${emoji}</span><h2 style="margin:0;font-size:20px;font-weight:700;color:#111;">${title}</h2></div>`;
  const content = items.map((i, idx) => `<div style="display:flex;align-items:flex-start;margin-bottom:16px;padding-bottom:16px;${idx < items.length - 1 ? 'border-bottom:1px solid #f3f4f6;' : ''}"><div style="flex:1;"><a href="${i.url}" style="text-decoration:none;color:#111;font-size:15px;font-weight:500;line-height:1.5;">${(i.title || '').substring(0,90)}</a><p style="margin:4px 0 0;font-size:13px;color:#6b7280;line-height:1.4;">${(i.summary || '').substring(0,100)}</p><div style="margin-top:6px;font-size:12px;color:#9ca3af;">${sourceBadge(i.source)}</div></div></div>`).join('');
  return `<div style="margin-bottom:40px;">${header}${content}</div>`;
};

const renderCommunity = (items) => {
  if (!items?.length) return '';
  return `<div style="margin-bottom:40px;"><div style="display:flex;align-items:center;margin-bottom:20px;"><span style="font-size:24px;margin-right:10px;">ðŸ’¬</span><h2 style="margin:0;font-size:20px;font-weight:700;color:#111;">Community Buzz</h2></div><div style="background:linear-gradient(135deg,#fef3c7 0%,#fef9c3 100%);border-radius:12px;padding:20px;">${items.map((i, idx) => `<div style="margin-bottom:14px;padding-bottom:14px;${idx < items.length - 1 ? 'border-bottom:1px solid rgba(0,0,0,0.1);' : ''}"><a href="${i.url}" style="text-decoration:none;color:#92400e;font-size:14px;font-weight:500;line-height:1.5;">"${(i.title || '').substring(0,80)}"</a><p style="margin:4px 0 0;font-size:12px;color:#a16207;">${(i.summary || '').substring(0,80)}</p></div>`).join('')}</div></div>`;
};

const dayName = new Date().toLocaleDateString('en-US', { weekday: 'long' });
const totalItems = Object.values(s).reduce((sum, arr) => sum + (arr?.length || 0), 0);

const topStory = s.TOP_STORIES?.[0] || s.BREAKING?.[0];
const tldr = topStory ? `<strong>Today's highlight:</strong> ${(topStory.title || '').substring(0, 100)}. Plus ${totalItems - 1} more stories across coding tools, AI assistants, research, and community discussions.` : `${totalItems} curated stories from across the AI world.`;

const html = `<!DOCTYPE html><html><head><meta charset="utf-8"><meta name="viewport" content="width=device-width, initial-scale=1"><title>vibecheck - ${d.date}</title><link href="https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700;800&display=swap" rel="stylesheet"></head><body style="margin:0;padding:0;background:#f5f5f7;font-family:'Inter',-apple-system,BlinkMacSystemFont,sans-serif;"><div style="max-width:680px;margin:0 auto;background:#fff;"><div style="background:linear-gradient(135deg,#6366f1 0%,#8b5cf6 50%,#a855f7 100%);padding:48px 40px;text-align:center;"><div style="font-size:14px;color:rgba(255,255,255,0.8);text-transform:uppercase;letter-spacing:2px;margin-bottom:8px;">${dayName} Edition</div><h1 style="margin:0;font-size:42px;font-weight:800;color:#fff;letter-spacing:-1px;">vibecheck</h1><p style="margin:12px 0 0;font-size:16px;color:rgba(255,255,255,0.9);">Your daily dose of AI, curated.</p><p style="margin:8px 0 0;font-size:13px;color:rgba(255,255,255,0.6);">${d.date} â€¢ ${totalItems} stories</p></div><div style="margin:0;padding:28px 40px;background:linear-gradient(180deg,#faf5ff 0%,#fff 100%);border-bottom:1px solid #e5e7eb;"><div style="font-size:12px;font-weight:700;color:#7c3aed;text-transform:uppercase;letter-spacing:1px;margin-bottom:12px;">âš¡ TLDR</div><p style="margin:0;font-size:15px;line-height:1.7;color:#374151;">${tldr}</p></div><div style="padding:32px 40px;">${renderTopStories(s.TOP_STORIES || s.BREAKING)}${renderSection('ðŸ’»', 'Coding & Dev Tools', s.CODING_TOOLS)}${renderSection('âœ¨', 'AI Assistants & LLMs', s.WRITING_TOOLS)}${renderSection('ðŸŽ¨', 'Creative Tools', s.CREATIVE_TOOLS)}${renderSection('ðŸ§ ', 'Life & Productivity', s.LIFE_PRODUCTIVITY)}${renderSection('ðŸ”¬', 'Research & Frontier', s.RESEARCH_FRONTIER)}${renderCommunity(s.COMMUNITY_PULSE)}</div><div style="background:#111;padding:40px;text-align:center;"><div style="font-size:28px;font-weight:800;color:#fff;margin-bottom:8px;">vibecheck</div><p style="color:#6b7280;font-size:13px;margin:0 0 20px;">Keeping you informed and autonomous.</p><div style="margin-bottom:20px;"><a href="https://n8n.wsig.me/webhook/vibecheck-signup" style="color:#6366f1;text-decoration:none;font-size:13px;margin:0 12px;">Preferences</a><a href="https://n8n.wsig.me/webhook/vibecheck-archive" style="color:#6366f1;text-decoration:none;font-size:13px;margin:0 12px;">Archive</a><a href="https://n8n.wsig.me/webhook/vibecheck-unsubscribe" style="color:#6366f1;text-decoration:none;font-size:13px;margin:0 12px;">Unsubscribe</a></div><p style="color:#4b5563;font-size:11px;margin:0;">Delivered daily at 8 AM ET â€¢ Powered by AI</p></div></div></body></html>`;

return [{ json: { html, ...d } }];

const digest = $('Render Newsletter HTML').first().json;
const subscribers = $('Get Subscribers').all();

const results = [];
for (const sub of subscribers) {
  results.push({
    json: {
      user_id: sub.json.id,
      email: sub.json.email,
      name: sub.json.name,
      delivery_method: sub.json.delivery_method,
      telegram_chat_id: sub.json.telegram_chat_id,
      discord_webhook: sub.json.discord_webhook,
      slack_webhook: sub.json.slack_webhook,
      html: digest.html,
      date: digest.date,
      sections: digest.sections
    }
  });
}

return results;

